Another in C# 3 (though the indentation may get destroyed):

using System.Collections.Generic;
using System.IO;
using System.Linq;

namespace ConsoleApplication1
{
    internal class Program
    {
        private static IEnumerable<string> GetDirectoryDescendants(string path)
        {
            return Directory.GetFiles(path)
                .Concat(Directory.GetDirectories(path)
                            .SelectMany(directory =&gt; GetDirectoryDescendants(directory)));
        }
    }
}</string>
