I simply copy &amp; paste your source code.
<br />
<br />        private void menuGetMoreRssThenMerge_Click(object sender, EventArgs e)
<br />        {
<br />            
<br />            NewDataSet tempDataSet = new NewDataSet();
<br />            tempDataSet.channel.channel_IdColumn.AutoIncrementSeed = GetNextChannelId();
<br />            tempDataSet.rss.rss_IdColumn.AutoIncrementSeed = GetNextRssId();
<br />
<br />            PopulateDataSet(tempDataSet, urlListB);
<br />            newDataSet.Merge(tempDataSet);
<br />            DisplayNumberOfChannels();
<br />
<br />        }
<br />
<br />        private int GetNextChannelId()
<br />        {
<br />            // Get idx of the last row of channel table
<br />            int idxLastChannelRow = newDataSet.channel.Rows.Count - 1;
<br />            // Get the channel_id from the last channel row
<br />            return newDataSet.channel[idxLastChannelRow].channel_Id + 1;
<br />        }
<br />
<br />        private int GetNextRssId()
<br />        {
<br />            // Get idx of the last row of rss table
<br />            int idxLastRssRow = newDataSet.rss.Rows.Count - 1;
<br />            // Get the rss_id from last rss row
<br />            return newDataSet.rss[idxLastRssRow].rss_Id + 1;
<br />        }
<br />
<br />        private void menuGetMoreRssReadDirectly_Click(object sender, EventArgs e)
<br />        {
<br />            PopulateDataSet(newDataSet, urlListB);
<br />            DisplayNumberOfChannels();
<br />        }
<br />
<br />        private void DisplayNumberOfChannels()
<br />        {
<br />            statusBar1.Text = "Number of Channels: " + 
<br />                (newDataSet != null &amp;&amp; newDataSet.channel != null ?
<br />                newDataSet.channel.Rows.Count.ToString() : "0");
<br />        }
