This details the approach we took for passing exceptions on the wire:
<br />
<br />&lt;http://www.microsoft.com/belux/fr/msdn/community/columns/jtielens/soapexceptions.mspx&gt;

Does this get more to the point? It gives back the strong typing to soap exceptions and allows the developer to still use the try / catch construct.
<br />
<br />
<br />
<br />using System;
<br />using System.Collections.Generic;
<br />using System.Text;
<br />using System.Reflection;
<br />using System.Security.Authentication;
<br />using System.Security;
<br />using System.Text.RegularExpressions;
<br />
<br />namespace exceptionClient
<br />{
<br />    class Program
<br />    {
<br />        static void Main(string[] args)
<br />        {
<br />            MyClass c = new MyClass();
<br />
<br />            try
<br />            {
<br />                c.CallThatMayFail("Bad Param");
<br />            }
<br />            catch (System.Web.Services.Protocols.SoapException soapex)
<br />            {
<br />                try
<br />                {
<br />                    ExceptionAdapter.Adapt(soapex);
<br />                }
<br />                catch (System.Security.Authentication.InvalidCredentialException icex)
<br />                {
<br />                    Console.WriteLine(icex.Message);
<br />                }
<br />            }
<br />
<br />            Console.Read();
<br />        }
<br />    }
<br />
<br />    class ExceptionAdapter
<br />    {
<br />        public static void Adapt(System.Web.Services.Protocols.SoapException ex)
<br />        {
<br />            MatchCollection mc = Regex.Matches(ex.Message, "---&gt; ([^:]+):");
<br />
<br />            if(mc.Count &gt;= 1)
<br />            {
<br />                if (mc[0].Groups.Count &gt; 1)
<br />                {
<br />                    if (mc[0].Groups[1].Value + "" != string.Empty)
<br />                    {
<br />                        Type t = null;
<br />
<br />                        foreach (Assembly asm in AppDomain.CurrentDomain.GetAssemblies())
<br />                        {
<br />                            t = asm.GetType(mc[0].Groups[1].Value);
<br />                            if (t != null)
<br />                                break;
<br />                        }
<br />
<br />                        if (t != null)
<br />                        {
<br />                            MatchCollection mcMsg = Regex.Matches(ex.Message,
<br />                                @"---&gt;[^:]+\:([^\n]+)\n");
<br />
<br />                            object oex = null;
<br />
<br />                            if (mcMsg.Count &gt;= 1)
<br />                            {
<br />                                if (mcMsg[0].Groups.Count &gt; 1)
<br />                                {
<br />                                    oex = System.Activator.CreateInstance(
<br />                                        t, new object[] { mcMsg[0].Groups[1].Value.Trim() });
<br />
<br />                                    throw (System.Exception)oex;
<br />                                }
<br />                            }
<br />
<br />                            oex = System.Activator.CreateInstance(t);
<br />                            throw (System.Exception)oex;
<br />
<br />                        }
<br />                    }
<br />                }
<br />            }
<br />        }
<br />    }
<br />}
